use std::io::{self, Write};

fn main() -> io::Result<()> {
    io::stderr().write(b"hello world")?;

    Ok(())
}

use std::io::{self, Write};

fn main() -> io::Result<()> {
    let stderr = io::stderr();
    let mut handle = stderr.lock();

    handle.write(b"hello world")?;

    Ok(())
}
