use std::mem;

unsafe {
    let s = String::from("hello");
    let ptr = s.as_ptr();
    let len = s.len();
    let capacity = s.capacity();

    mem::forget(s);

    let s = String::from_raw_parts(ptr as *mut _, len, capacity);

    assert_eq!(String::from("hello"), s);
}

// some bytes, in a vector
let sparkle_heart = vec![240, 159, 146, 150];

let sparkle_heart = unsafe {
    String::from_utf8_unchecked(sparkle_heart)
};

assert_eq!("üíñ", sparkle_heart);

let s = String::from("hello");
let bytes = s.into_bytes();

assert_eq!(&[104, 101, 108, 108, 111][..], &bytes[..]);

let mut s = String::from("foobar");
let s_mut_str = s.as_mut_str();

s_mut_str.make_ascii_uppercase();

assert_eq!("FOOBAR", s_mut_str);

let mut s = String::from("foo");

s.push_str("bar");

assert_eq!("foobar", s);

let mut s = String::new();

s.reserve(10);

assert!(s.capacity() >= 10);

let mut s = String::with_capacity(10);
s.push('a');
s.push('b');

// s now has a length of 2 and a capacity of 10
assert_eq!(2, s.len());
assert_eq!(10, s.capacity());

// Since we already have an extra 8 capacity, calling this...
s.reserve(8);

// ... doesn't actually increase.
assert_eq!(10, s.capacity());

let mut s = String::new();

s.reserve_exact(10);

assert!(s.capacity() >= 10);

let mut s = String::with_capacity(10);
s.push('a');
s.push('b');

// s now has a length of 2 and a capacity of 10
assert_eq!(2, s.len());
assert_eq!(10, s.capacity());

// Since we already have an extra 8 capacity, calling this...
s.reserve_exact(8);

// ... doesn't actually increase.
assert_eq!(10, s.capacity());

let mut s = String::from("foo");

s.reserve(100);
assert!(s.capacity() >= 100);

s.shrink_to_fit();
assert_eq!(3, s.capacity());

let mut s = String::from("abc");

s.push('1');
s.push('2');
s.push('3');

assert_eq!("abc123", s);

let mut s = String::from("hello");

s.truncate(2);

assert_eq!("he", s);

let mut s = String::from("foo");

assert_eq!(s.pop(), Some('o'));
assert_eq!(s.pop(), Some('o'));
assert_eq!(s.pop(), Some('f'));

assert_eq!(s.pop(), None);

let mut s = String::from("foo");

assert_eq!(s.remove(0), 'f');
assert_eq!(s.remove(1), 'o');
assert_eq!(s.remove(0), 'o');

let mut s = String::from("f_o_ob_ar");

s.retain(|c| c != '_');

assert_eq!(s, "foobar");

let mut s = String::with_capacity(3);

s.insert(0, 'f');
s.insert(1, 'o');
s.insert(2, 'o');

assert_eq!("foo", s);

let mut s = String::from("bar");

s.insert_str(0, "foo");

assert_eq!("foobar", s);

let mut s = String::from("hello");

unsafe {
    let vec = s.as_mut_vec();
    assert_eq!(&[104, 101, 108, 108, 111][..], &vec[..]);

    vec.reverse();
}
assert_eq!(s, "olleh");

let mut v = String::new();
assert!(v.is_empty());

v.push('a');
assert!(!v.is_empty());

let mut hello = String::from("Hello, World!");
let world = hello.split_off(7);
assert_eq!(hello, "Hello, ");
assert_eq!(world, "World!");

let mut s = String::from("foo");

s.clear();

assert!(s.is_empty());
assert_eq!(0, s.len());
assert_eq!(3, s.capacity());

let mut s = String::from("Œ± is alpha, Œ≤ is beta");
let beta_offset = s.find('Œ≤').unwrap_or(s.len());

// Remove the range up until the Œ≤ from the string
let t: String = s.drain(..beta_offset).collect();
assert_eq!(t, "Œ± is alpha, ");
assert_eq!(s, "Œ≤ is beta");

// A full range clears the string
s.drain(..);
assert_eq!(s, "");

let mut s = String::from("Œ± is alpha, Œ≤ is beta");
let beta_offset = s.find('Œ≤').unwrap_or(s.len());

// Replace the range up until the Œ≤ from the string
s.replace_range(..beta_offset, "Œë is capital alpha; ");
assert_eq!(s, "Œë is capital alpha; Œ≤ is beta");
